# -*-coding:utf-8 -*-from django import formsfrom django.forms import ModelFormfrom .models import Hostclass LoginForm(forms.Form):    username = forms.CharField()    password = forms.CharField(widget=forms.PasswordInput)class hostinfo (forms.Form):    class Meta:        model=Host        fields=('hostname','ip','memory','disk','cpu_core')        class UploadFileForm(forms.Form):    #uploadtitle = forms.CharField(label=u'文件名',max_length=200)    file = forms.FileField()