# -*-coding:utf-8 -*-from django import formsfrom django.contrib.auth.forms import AuthenticationFormfrom django.utils.translation import gettext_lazy as _from captcha.fields import CaptchaFieldclass UserLoginForm(AuthenticationForm):    username = forms.CharField(label=_('Username'), max_length=100)    password = forms.CharField(        label=_('Password'), widget=forms.PasswordInput, max_length=100,        strip=False)class RegisterForm(forms.Form):    username = forms.CharField(required=True,widget=forms.TextInput(attrs={"class": "form-control", "placeholder": "Username", "required": "","type":"email"}),max_length=50)    password = forms.CharField(required=True,widget=forms.PasswordInput(attrs={"class": "form-control", "placeholder": "Password", "required": "","type":"password"}),max_length=50)    captcha = CaptchaField(error_messages={'invalid':u'验证码错误'}),